import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { updatePost, selectAllPosts } from '../store/slices/postsSlice';
import Sidebar from '../components/layout/Sidebar';
import RightSidebar from '../components/layout/RightSidebar';

const EditPost = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const dispatch = useDispatch();
    const posts = useSelector(selectAllPosts);
    const post = posts.find(p => p.id === parseInt(id));

    const [content, setContent] = useState('');
    const [image, setImage] = useState('');

    useEffect(() => {
        if (post) {
            setContent(post.content);
            setImage(post.image);
        }
    }, [post]);

    const handleUpdate = (e) => {
        e.preventDefault();
        dispatch(updatePost({ id: parseInt(id), data: { content, image } }));
        navigate('/my-posts');
    };

    if (!post) return <p>Post not found...</p>;

    return (
        <div className="home-container">
            <Sidebar />
            <div className="main-content">
                <div className="write-post-container" style={{ padding: '20px' }}>
                    <h2>Edit Post</h2>
                    <form onSubmit={handleUpdate}>
                        <textarea
                            rows="5"
                            style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}
                            value={content}
                            onChange={(e) => setContent(e.target.value)}
                        ></textarea>
                        <input
                            type="text"
                            placeholder="Image URL"
                            style={{ width: '100%', margin: '10px 0', padding: '10px' }}
                            value={image}
                            onChange={(e) => setImage(e.target.value)}
                        />
                        <div style={{ display: 'flex', gap: '10px' }}>
                            <button type="submit" style={{ background: '#1876f2', color: 'white', padding: '10px 20px', border: 'none', borderRadius: '5px' }}>Save Changes</button>
                            <button type="button" onClick={() => navigate(-1)} style={{ background: '#ccc', padding: '10px 20px', border: 'none', borderRadius: '5px' }}>Cancel</button>
                        </div>
                    </form>
                </div>
            </div>
            <RightSidebar />
        </div>
    );
};

export default EditPost;
