import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createPost } from '../../store/slices/postsSlice';
import { selectCurrentUser } from '../../store/slices/authSlice';
import photoIcon from '../../assets/photo.png';
import feelingIcon from '../../assets/feeling.png';

const WritePost = () => {
    const [content, setContent] = useState('');
    const [image, setImage] = useState('');
    const [hashtags, setHashtags] = useState('');
    const currentUser = useSelector(selectCurrentUser);
    const dispatch = useDispatch();

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!currentUser) return alert("Please login first");

        const newPost = {
            content,
            image: image || "https://picsum.photos/400/300",
            authorId: currentUser.id,
            hashtags: hashtags.split(',').map(s => s.trim()).filter(s => s !== ''),
            likes: [],
            date: new Date().toLocaleString()
        };

        dispatch(createPost(newPost));
        setContent('');
        setImage('');
        setHashtags('');
    };

    if (!currentUser) return null;

    return (
        <div className="write-post-container">
            <div className="user-profile">
                <img src={currentUser.avatar} alt="user" />
                <div>
                    <p>{currentUser.username}</p>
                    <small>Public <i className="fa-solid fa-caret-down"></i></small>
                </div>
            </div>
            <div className="post-input-container">
                <textarea
                    rows="3"
                    placeholder={`What's on your mind, ${currentUser.username}?`}
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                ></textarea>
                <div style={{ marginTop: '10px' }}>
                    <input
                        type="text"
                        placeholder="Image URL (optional)"
                        value={image}
                        onChange={(e) => setImage(e.target.value)}
                        style={{ width: '100%', marginBottom: '5px', padding: '5px' }}
                    />
                    <input
                        type="text"
                        placeholder="Hashtags (comma separated)"
                        value={hashtags}
                        onChange={(e) => setHashtags(e.target.value)}
                        style={{ width: '100%', padding: '5px' }}
                    />
                </div>
                <div className="add-post-links">
                    <a href="#" onClick={handleSubmit} style={{ background: '#1876f2', color: 'white', padding: '5px 15px', borderRadius: '5px' }}>Post</a>
                    <a href="#"><img src={photoIcon} alt="photo" />Photo/Video</a>
                    <a href="#"><img src={feelingIcon} alt="feeling" />Feeling/Activity</a>
                </div>
            </div>
        </div>
    );
};

export default WritePost;
