import React from 'react';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { selectCurrentUser } from '../../store/slices/authSlice';

const Sidebar = () => {
    const currentUser = useSelector(selectCurrentUser);

    return (
        <div className="left-sidebar">
            <div className="imp-links">
                <Link to="/"><img src="/news.png" alt="posts" /> Posts</Link>
                <Link to="#"><img src="/friends.png" alt="friends" /> Friends</Link>
                <Link to="#"><img src="/group.png" alt="group" /> Group</Link>
                <Link to="#"><img src="/marketplace.png" alt="market" /> Market-place</Link>
                <Link to="#"><img src="/watch.png" alt="reels" /> Reels</Link>
                <Link to="#" id="rasha">See More</Link>
            </div>
            <div className="shortcut-links">
                <p>Trending Hashtags</p>
                <Link to="/?hashtag=react" style={{ display: 'block', marginBottom: '10px' }}>#react</Link>
                <Link to="/?hashtag=frontend" style={{ display: 'block', marginBottom: '10px' }}>#frontend</Link>
                <Link to="/?hashtag=CHESS" style={{ display: 'block', marginBottom: '10px' }}>#CHESS</Link>
                <p>Your Shortcuts</p>
                <Link to="#"><img src="/shortcut-1.png" alt="s1" /> Web Developers</Link>
                <Link to="#"><img src="/shortcut-2.png" alt="s2" /> Web Design course</Link>
                {currentUser && (
                    <Link to="/my-posts" className="active">My Posts</Link>
                )}
            </div>
        </div>
    );
};

export default Sidebar;
