import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { setCurrentUser, selectCurrentUser } from '../store/slices/authSlice';

const Login = () => {
    const [username, setUsername] = useState('');
    const [users, setUsers] = useState([]);
    const dispatch = useDispatch();
    const navigate = useNavigate();
    const currentUser = useSelector(selectCurrentUser);

    useEffect(() => {
        if (currentUser) {
            navigate('/');
        }
        const fetchUsers = async () => {
            try {
                const response = await api.get('/users');
                setUsers(response.data);
            } catch (err) {
                console.error("Failed to fetch users", err);
            }
        };
        fetchUsers();
    }, [currentUser, navigate]);

    const handleLogin = (user) => {
        dispatch(setCurrentUser(user));
        navigate('/');
    };

    const handleCustomLogin = (e) => {
        e.preventDefault();
        const existingUser = users.find(u => u.username.toLowerCase() === username.toLowerCase());
        if (existingUser) {
            handleLogin(existingUser);
        } else {
            alert("User not found mapping to simulate login");
        }
    };

    return (
        <div className="login-container">
            <div className="login-card">
                <h2>Login to Mini Social</h2>
                <form onSubmit={handleCustomLogin}>
                    <input
                        type="text"
                        placeholder="Enter username (e.g., sara, youssef)"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                    />
                    <button type="submit">Login</button>
                </form>

                <div className="quick-login">
                    <p>Or click to login as:</p>
                    <div className="user-list">
                        {users.map(user => (
                            <div key={user.id} className="user-item" onClick={() => handleLogin(user)}>
                                <img src={user.avatar} alt={user.username} />
                                <span>{user.username}</span>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Login;
