import React from 'react';
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { toggleLike, deletePost } from '../../features/posts/postsSlice';
import { selectCurrentUser } from '../../features/auth/authSlice';
import defaultAvatar from '../../assets/wp1923705.jpg';
import likeBlueIcon from '../../assets/like-blue.png';
import likeIcon from '../../assets/like.png';
import commentsIcon from '../../assets/comments.png';
import shareIcon from '../../assets/share.png';

const PostCard = ({ post }) => {
    const dispatch = useDispatch();
    const currentUser = useSelector(selectCurrentUser);
    const isLiked = currentUser && post.likes && post.likes.includes(currentUser.id);
    const isAuthor = currentUser && currentUser.id === post.authorId;

    const handleLike = () => {
        if (currentUser) {
            dispatch(toggleLike({ postId: post.id, userId: currentUser.id }));
        } else {
            alert("Please login to like posts");
        }
    };

    const handleDelete = (e) => {
        e.stopPropagation();
        e.preventDefault();
        if (window.confirm("Are you sure you want to delete this post?")) {
            dispatch(deletePost(post.id));
        }
    };

    return (
        <div className="post-container">
            <div className="post-row">
                <div className="user-profile">
                    <img src={post.user?.avatar || defaultAvatar} alt="avatar" />
                    <div>
                        <p>{post.user?.username || 'Unknown'}</p>
                        <span>{post.date || 'Just now'}</span>
                    </div>
                </div>
                <div style={{ display: 'flex', gap: '15px', alignItems: 'center' }}>
                    {isAuthor && (
                        <>
                            <Link to={`/posts/edit/${post.id}`} title="Edit Post">
                                <i className="fas fa-edit" style={{ color: '#1876f2' }}></i>
                            </Link>
                            <i
                                className="fas fa-trash-alt"
                                style={{ color: '#ff4d4f', cursor: 'pointer' }}
                                onClick={handleDelete}
                                title="Delete Post"
                            ></i>
                        </>
                    )}
                </div>
            </div>

            <Link to={`/post/${post.id}`} style={{ textDecoration: 'none', color: 'inherit' }}>
                <p className="post-text">
                    {post.content}
                    {post.hashtags && post.hashtags.map(h => (
                        <span key={h} style={{ marginLeft: '5px', color: '#1876f2' }}>#{h}</span>
                    ))}
                </p>
                {post.image && <img src={post.image} className="post-img" alt="post" />}
            </Link>

            <div className="post-row">
                <div className="activity-icons">
                    <div onClick={handleLike} style={{ cursor: 'pointer', color: isLiked ? '#1876f2' : 'inherit' }}>
                        <img src={isLiked ? likeBlueIcon : likeIcon} alt="like" />
                        {post.likes?.length || 0}
                    </div>
                    <div><img src={commentsIcon} alt="comments" /> 2</div>
                    <div><img src={shareIcon} alt="share" /> 0</div>
                </div>
                <div className="post-profile-icon">
                    <img src={currentUser?.avatar || defaultAvatar} alt="user" />
                    <i className="fa-solid fa-caret-down"></i>
                </div>
            </div>
        </div >
    );
};

export default PostCard;
